CREATE VIEW vista_bandejaentradamensajescashmanha  AS SELECT mensajeria.idmensajeria AS idmensajeria, mensajeria.idusuarios AS idusuarios, mensajeria.idusuariosdestinatario AS idusuariosdestinatario, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.fotoperfil AS fotoperfil, mensajeria.nombremensaje AS nombremensaje, mensajeria.asuntomensaje AS asuntomensaje, mensajeria.detallemensaje AS detallemensaje, mensajeria.fechamensaje AS fechamensaje, mensajeria.ocultarmensaje AS ocultarmensaje FROM (mensajeria left join usuarios on(mensajeria.idusuarios = usuarios.idusuarios));


CREATE VIEW vista_calculaduracioncodigoseguridad_transferencias  AS SELECT codigostransferencias.idcodigo AS idcodigo, codigostransferencias.codigoseguridad AS codigoseguridad, concat(timestampdiff(MINUTE,codigostransferencias.fecha,current_timestamp())) AS minutos_expiracion, codigostransferencias.estado AS estado, codigostransferencias.idusuarios AS idusuarios FROM codigostransferencias ;


CREATE VIEW vista_calculocuentasahorooregistradas_interfaces  AS SELECT count(0) AS numero_cuentasahorro_registradas FROM cuentas  ;


CREATE VIEW vista_calculocuotasregistradas_interfaces  AS SELECT count(0) AS numero_cuotas_registrados FROM cuotas ;


CREATE VIEW vista_calculocuotasvencidas_interfaces  AS SELECT count(0) AS numero_cuotas_vencidas FROM cuotas WHERE cuotas.incumplimiento_pago = 'SI''SI'  ;


CREATE VIEW vista_calculodiasfechavencimiento_cuotasclientes  AS SELECT cuotas.idcuotas AS idcuotas, cuotas.idusuarios AS idusuarios, cuotas.idproducto AS idproducto, cuotas.montocancelar AS montocancelar, cuotas.estadocuota AS estadocuota, cuotas.fechavencimiento AS fechavencimiento, cuotas.incumplimiento_pago AS incumplimiento_pago, to_days(current_timestamp()) - to_days(cuotas.fechavencimiento) AS dias_incumplimiento FROM cuotas  ;


CREATE VIEW vista_calculoexpiracion_codigocambiocredencialesusuarios  AS SELECT recuperacion.idrecuperaciones AS idrecuperaciones, recuperacion.correo AS correo, recuperacion.token AS token, recuperacion.codigo AS codigo, concat(timestampdiff(MINUTE,recuperacion.fecha,current_timestamp())) AS minutos_expiracion, recuperacion.estado AS estado FROM recuperacion  ;


CREATE VIEW vista_calculoingresostransacciones_empleadosatencionclientes  AS SELECT transacciones.empleado_gestion AS empleado_gestion, sum(transacciones.monto) AS monto_transacciones_empleados FROM transacciones GROUP BY transacciones.empleado_gestion  ;


CREATE VIEW vista_calculonumerocuotascanceladas_interfaces  AS SELECT count(0) AS numero_cuotas_canceladas FROM cuotas WHERE cuotas.estadocuota = 'cancelado''cancelado'  ;


CREATE VIEW vista_calculonumerocuotaspagadastarde_interfaces  AS SELECT count(0) AS numero_cuotas_pagadas_tarde FROM cuotas WHERE cuotas.incumplimiento_pago = 'PT''PT'  ;



CREATE VIEW vista_calculonumerotransferencias_interfaces  AS SELECT count(0) AS numero_transferencias FROM transferencias  ;


CREATE VIEW vista_calculoproductosregistrados_interfaces  AS SELECT count(0) AS numero_productos_registrados FROM productos  ;


CREATE VIEW vista_calculotransaccionescreditos_interfaces  AS SELECT count(0) AS numero_transacciones_creditos FROM transacciones  ;


CREATE VIEW vista_calculo_ultimaactividad_ticketsreportesplataforma  AS SELECT reporteproblemasplataforma.idreporte AS idreporte, reporteproblemasplataforma.idusuarios AS idusuarios, usuarios.codigousuario AS codigousuario, reporteproblemasplataforma.estado AS estado, to_days(current_timestamp()) - to_days(reporteproblemasplataforma.fechaactualizacionreporte) AS dias_ultima_actividad FROM (reporteproblemasplataforma join usuarios on(reporteproblemasplataforma.idusuarios = usuarios.idusuarios))  ;


CREATE VIEW vista_codigosseguridadtransferenciasclientes  AS SELECT codigostransferencias.idcodigo AS idcodigo, codigostransferencias.codigoseguridad AS codigoseguridad, codigostransferencias.fecha AS fecha, codigostransferencias.estado AS estado, codigostransferencias.idusuarios AS idusuarios FROM codigostransferencias  ;


CREATE VIEW vista_configuracionusuariosgeneral  AS SELECT usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.correo AS correo, usuarios.fotoperfil AS fotoperfil, usuarios.estado_usuario AS estado_usuario FROM usuarios  ;

CREATE VIEW vista_consultacalculocuotascanceladas_creditosclientes  AS SELECT cuotas.idcreditos AS idcreditos, cuotas.idusuarios AS idusuarios, cuotas.idproducto AS idproducto, cuotas.montocapital AS montocapital, count(0) AS cuotas_canceladas FROM cuotas WHERE cuotas.estadocuota = 'cancelado' GROUP BY cuotas.idusuarios ;

CREATE VIEW vista_consultacalculoavancecreditos_interfazclientes  AS SELECT creditos.idcreditos AS idcreditos, creditos.idusuarios AS idusuarios, creditos.idproducto AS idproducto, productos.nombreproducto AS nombreproducto, productos.codigo AS codigo, creditos.interescredito AS interescredito, creditos.cuotamensual AS cuotamensual, creditos.montocredito AS montocredito, creditos.plazocredito AS plazocredito, creditos.enviaralhistorico AS enviaralhistorico, vista_consultacalculocuotascanceladas_creditosclientes.cuotas_canceladas AS cuotas_canceladas, vista_consultacalculocuotascanceladas_creditosclientes.montocapital AS montocapital FROM ((creditos join productos on(creditos.idproducto = productos.idproducto)) join vista_consultacalculocuotascanceladas_creditosclientes on(vista_consultacalculocuotascanceladas_creditosclientes.idusuarios = creditos.idusuarios))  ;



CREATE VIEW vista_consultaclientesmorosos  AS SELECT vista_calculodiasfechavencimiento_cuotasclientes.idcuotas AS idcuotas, usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, detalleusuarios.dui AS dui, productos.nombreproducto AS nombreproducto, vista_calculodiasfechavencimiento_cuotasclientes.montocancelar AS montocancelar, vista_calculodiasfechavencimiento_cuotasclientes.estadocuota AS estadocuota, vista_calculodiasfechavencimiento_cuotasclientes.fechavencimiento AS fechavencimiento, vista_calculodiasfechavencimiento_cuotasclientes.incumplimiento_pago AS incumplimiento_pago, vista_calculodiasfechavencimiento_cuotasclientes.dias_incumplimiento AS dias_incumplimiento FROM (((vista_calculodiasfechavencimiento_cuotasclientes join usuarios on(vista_calculodiasfechavencimiento_cuotasclientes.idusuarios = usuarios.idusuarios)) join productos on(vista_calculodiasfechavencimiento_cuotasclientes.idproducto = productos.idproducto)) join detalleusuarios on(usuarios.idusuarios = detalleusuarios.idusuarios))  ;


CREATE VIEW vista_consultaclientes_creditoscancelados  AS SELECT historicocreditos.idhistorico AS idhistorico, historicocreditos.idusuarios AS idusuarios, historicocreditos.idproducto AS idproducto, historicocreditos.idcreditos AS idcreditos, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, usuarios.fotoperfil AS fotoperfil, productos.codigo AS codigo, productos.nombreproducto AS nombreproducto, historicocreditos.montocredito AS montocredito, historicocreditos.interescredito AS interescredito, historicocreditos.plazocredito AS plazocredito, historicocreditos.cuotamensual AS cuotamensual, historicocreditos.estado AS estado FROM (((historicocreditos join usuarios on(historicocreditos.idusuarios = usuarios.idusuarios)) join productos on(productos.idproducto = historicocreditos.idproducto)) join detalleusuarios on(detalleusuarios.idusuarios = historicocreditos.idusuarios))  ;


CREATE VIEW vista_consultacompletanuevassolicitudescreditosclientesgestiones  AS SELECT usuarios.idusuarios AS idusuarios, creditos.idcreditos AS idcreditos, creditos.idproducto AS idproducto, creditos.tipocliente AS tipocliente, creditos.montocredito AS montocredito, creditos.interescredito AS interescredito, creditos.plazocredito AS plazocredito, creditos.cuotamensual AS cuotamensual, creditos.saldocredito AS saldocredito, creditos.fechasolicitud AS fechasolicitud, creditos.salariocliente AS salariocliente, creditos.progreso_solicitud AS progreso_solicitud, creditos.estado AS estado, creditos.observaciones AS observaciones, creditos.observacion_gerencia AS observacion_gerencia, creditos.observacion_presidencia AS observacion_presidencia, creditos.usuario_empleado AS usuario_empleado, creditos.cuotas_generadas AS cuotas_generadas, creditos.estadocrediticio AS estadocrediticio, creditos.proceso_finalizado AS proceso_finalizado, creditos.enviaralhistorico AS enviaralhistorico, creditos.creditoactivo AS creditoactivo, productos.codigo AS codigo, productos.nombreproducto AS nombreproducto, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.fotoperfil AS fotoperfil, usuarios.idrol AS idrol, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, detalleusuarios.telefono AS telefono, detalleusuarios.celular AS celular, detalleusuarios.telefonotrabajo AS telefonotrabajo, detalleusuarios.direccion AS direccion, detalleusuarios.empresa AS empresa, detalleusuarios.cargo AS cargo, detalleusuarios.direcciontrabajo AS direcciontrabajo, detalleusuarios.fechanacimiento AS fechanacimiento, referenciaspersonales.nombres_referencia1 AS nombres_referencia1, referenciaspersonales.apellidos_referencia1 AS apellidos_referencia1, referenciaspersonales.empresa_referencia1 AS empresa_referencia1, referenciaspersonales.profesion_oficioreferencia1 AS profesion_oficioreferencia1, referenciaspersonales.telefono_referencia1 AS telefono_referencia1, referenciaspersonales.nombres_referencia2 AS nombres_referencia2, referenciaspersonales.apellidos_referencia2 AS apellidos_referencia2, referenciaspersonales.empresa_referencia2 AS empresa_referencia2, referenciaspersonales.profesion_oficioreferencia2 AS profesion_oficioreferencia2, referenciaspersonales.telefono_referencia2 AS telefono_referencia2 FROM ((((creditos join usuarios on(creditos.idusuarios = usuarios.idusuarios)) join detalleusuarios on(detalleusuarios.idusuarios = usuarios.idusuarios)) join referenciaspersonales on(referenciaspersonales.idusuarios = usuarios.idusuarios)) join productos on(productos.idproducto = creditos.idproducto))  ;


CREATE VIEW vista_consultacompletausuariosdetalles  AS SELECT usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.correo AS correo, usuarios.fotoperfil AS fotoperfil, usuarios.idrol AS idrol, usuarios.estado_usuario AS estado_usuario, usuarios.completoperfil AS completoperfil, usuarios.quienregistro AS quienregistro, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, detalleusuarios.telefono AS telefono, detalleusuarios.celular AS celular, detalleusuarios.telefonotrabajo AS telefonotrabajo, detalleusuarios.direccion AS direccion, detalleusuarios.empresa AS empresa, detalleusuarios.cargo AS cargo, detalleusuarios.direcciontrabajo AS direcciontrabajo, detalleusuarios.fechanacimiento AS fechanacimiento, detalleusuarios.genero AS genero, detalleusuarios.estadocivil AS estadocivil, detalleusuarios.fotoduifrontal AS fotoduifrontal, detalleusuarios.fotoduireverso AS fotoduireverso, detalleusuarios.fotonit AS fotonit, detalleusuarios.fotofirma AS fotofirma FROM (usuarios left join detalleusuarios on(detalleusuarios.idusuarios = usuarios.idusuarios))  ;


