CREATE VIEW vista_consultacuotasgeneradas_creditosaprobadosclientes  AS SELECT cuotas.idcuotas AS idcuotas, usuarios.idusuarios AS idusuarios, productos.idproducto AS idproducto, creditos.idcreditos AS idcreditos, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.fotoperfil AS fotoperfil, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, creditos.montocredito AS montocredito, creditos.interescredito AS interescredito, creditos.plazocredito AS plazocredito, creditos.cuotamensual AS cuotamensual, cuotas.montocancelar AS montocancelar, cuotas.estadocuota AS estadocuota, cuotas.nombreproducto AS nombreproducto, cuotas.montocapital AS montocapital, cuotas.fechavencimiento AS fechavencimiento, cuotas.incumplimiento_pago AS incumplimiento_pago, to_days(current_timestamp()) - to_days(cuotas.fechavencimiento) AS dias_incumplimiento FROM ((((cuotas join usuarios on(cuotas.idusuarios = usuarios.idusuarios)) join productos on(productos.idproducto = cuotas.idproducto)) join detalleusuarios on(detalleusuarios.idusuarios = usuarios.idusuarios)) join creditos on(creditos.idusuarios = usuarios.idusuarios))  ;


CREATE VIEW vista_consultacuotashistoricocreditosclientes  AS SELECT historicotransacciones.idhistoricotransaccion, historicotransacciones.idusuarios, historicotransacciones.idproducto, historicotransacciones.idcreditos, historicotransacciones.idcuotas, historicocreditos.montocredito, historicocreditos.interescredito, historicocreditos.plazocredito, historicocreditos.cuotamensual, historicotransacciones.monto, historicotransacciones.referencia, historicotransacciones.monto, historicotransacciones.fecha, historicotransacciones.dias_incumplimiento, historicotransacciones.empleado_gestion FROM historicotransacciones INNER JOIN historicocreditos ON historicotransacciones.idcreditos = historicocreditos.idcreditos;

CREATE VIEW vista_consultadatosprestamosdevehiculosclientes  AS SELECT datosvehiculoscreditos.iddatosvehiculos AS iddatosvehiculos, datosvehiculoscreditos.idcreditos AS idcreditos, datosvehiculoscreditos.idproducto AS idproducto, datosvehiculoscreditos.idusuarios AS idusuarios, datosvehiculoscreditos.placa AS placa, datosvehiculoscreditos.clase AS clase, datosvehiculoscreditos.anio AS anio, datosvehiculoscreditos.capacidad AS capacidad, datosvehiculoscreditos.asientos AS asientos, datosvehiculoscreditos.marca AS marca, datosvehiculoscreditos.modelo AS modelo, datosvehiculoscreditos.numeromotor AS numeromotor, datosvehiculoscreditos.chasisgrabado AS chasisgrabado, datosvehiculoscreditos.chasisvin AS chasisvin, datosvehiculoscreditos.color AS color FROM datosvehiculoscreditos  ;

CREATE VIEW vista_consultadetallecompletotransaccionescuentasclientes  AS SELECT transaccionescuentasclientes.idtransaccioncuentas AS idtransaccioncuentas, transaccionescuentasclientes.idusuarios AS idusuarios, transaccionescuentasclientes.idproducto AS idproducto, transaccionescuentasclientes.idcuentas AS idcuentas, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, productos.codigo AS codigo, productos.nombreproducto AS nombreproducto, cuentas.numerocuenta AS numerocuenta, transaccionescuentasclientes.referencia AS referencia, transaccionescuentasclientes.monto AS monto, transaccionescuentasclientes.fecha AS fecha, transaccionescuentasclientes.empleado_gestion AS empleado_gestion, transaccionescuentasclientes.tipotransaccion AS tipotransaccion, transaccionescuentasclientes.estadotransaccion AS estadotransaccion FROM (((transaccionescuentasclientes join usuarios on(transaccionescuentasclientes.idusuarios = usuarios.idusuarios)) join productos on(transaccionescuentasclientes.idproducto = productos.idproducto)) join cuentas on(transaccionescuentasclientes.idcuentas = cuentas.idcuentas))  ;

CREATE VIEW vista_consultadetallesinterfazpresidencia  AS SELECT vista_calculoproductosregistrados_interfaces.numero_productos_registrados AS numero_productos_registrados, vista_calculocuotasregistradas_interfaces.numero_cuotas_registrados AS numero_cuotas_registrados, vista_calculocuentasahorooregistradas_interfaces.numero_cuentasahorro_registradas AS numero_cuentasahorro_registradas, vista_calculotransaccionescreditos_interfaces.numero_transacciones_creditos AS numero_transacciones_creditos FROM (((vista_calculoproductosregistrados_interfaces left join vista_calculocuotasregistradas_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculocuentasahorooregistradas_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculotransaccionescreditos_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados))  ;


CREATE VIEW vista_consultadetallessolicitudescreditosaprobadosclientes  AS SELECT usuarios.idusuarios AS idusuarios, creditos.idcreditos AS idcreditos, creditos.idproducto AS idproducto, creditos.montocredito AS montocredito, creditos.interescredito AS interescredito, creditos.plazocredito AS plazocredito, creditos.cuotamensual AS cuotamensual, creditos.fechasolicitud AS fechasolicitud, creditos.estado AS estado, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.correo AS correo, usuarios.fotoperfil AS fotoperfil, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit FROM ((creditos join usuarios on(creditos.idusuarios = usuarios.idusuarios)) join detalleusuarios on(detalleusuarios.idusuarios = usuarios.idusuarios))  ;


CREATE VIEW vista_consultaestadosolicitudcredito_portalclientesbienvenida  AS SELECT creditos.idusuarios AS idusuarios, creditos.estado AS estado, creditos.progreso_solicitud AS progreso_solicitud, productos.nombreproducto AS nombreproducto, productos.codigo AS codigo FROM ((creditos join usuarios on(creditos.idusuarios = usuarios.idusuarios)) join productos on(creditos.idproducto = productos.idproducto))  ;


CREATE VIEW vista_consultaexistenciacuotasmensualesclientes  AS SELECT cuotas.idcuotas AS idcuotas, cuotas.idcreditos AS idcreditos, cuotas.idproducto AS idproducto, cuotas.idusuarios AS idusuarios, cuotas.nombreproducto AS nombreproducto FROM cuotas  ;


