CREATE VIEW vista_consultageneralreestructuracioncreditosclientes  AS SELECT a.idusuarios AS idusuarios, a.idcreditos AS idcreditos, a.montocredito AS montocredito, a.interescredito AS interescredito, a.plazocredito AS plazocredito, a.cuotamensual AS cuotamensual, a.estado AS estado, a.fechasolicitud AS fechasolicitud, a.cuotas_generadas AS cuotas_generadas, b.nombres AS nombres, b.apellidos AS apellidos, b.codigousuario AS codigousuario, b.fotoperfil AS fotoperfil, c.dui AS dui, c.nit AS nit FROM ((creditos a join usuarios b on(a.idusuarios = b.idusuarios)) join detalleusuarios c on(a.idusuarios = c.idusuarios))  ;

