CREATE VIEW vista_consultanuevo_prestamopersonal_asignado_clientes  AS SELECT creditos.idproducto AS idproducto, creditos.idcreditos AS idcreditos, productos.codigo AS codigo, productos.nombreproducto AS nombreproducto, creditos.fechasolicitud AS fechasolicitud, creditos.montocredito AS montocredito, creditos.interescredito AS interescredito, creditos.plazocredito AS plazocredito, creditos.progreso_solicitud AS progreso_solicitud, creditos.progreso_pagocredito AS progreso_pagocredito, creditos.estado AS estado, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.idusuarios AS idusuarios FROM ((creditos join usuarios on(creditos.idusuarios = usuarios.idusuarios)) join productos on(productos.idproducto = creditos.idproducto))  ;

