CREATE VIEW vista_consultardisponibilidadnuevoscreditosclientes  AS SELECT usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.habilitarnuevoscreditos AS habilitarnuevoscreditos FROM usuarios  ;


CREATE VIEW vista_consultareportesfallosplataforma  AS SELECT reporteproblemasplataforma.idreporte AS idreporte, reporteproblemasplataforma.idusuarios AS idusuarios, usuarios.codigousuario AS codigousuario, reporteproblemasplataforma.nombrereporte AS nombrereporte, reporteproblemasplataforma.descripcionreporte AS descripcionreporte, reporteproblemasplataforma.fotoreporte AS fotoreporte, reporteproblemasplataforma.fecharegistroreporte AS fecharegistroreporte, reporteproblemasplataforma.fechaactualizacionreporte AS fechaactualizacionreporte, reporteproblemasplataforma.estado AS estado, reporteproblemasplataforma.comentarioactualizacion AS comentarioactualizacion, reporteproblemasplataforma.empleado_gestion AS empleado_gestion FROM (reporteproblemasplataforma join usuarios on(reporteproblemasplataforma.idusuarios = usuarios.idusuarios))  ;


CREATE VIEW vista_consultatransaccionesclientesgeneral  AS SELECT cuotas.idcuotas AS idcuotas, transacciones.idusuarios AS idusuarios, transacciones.referencia AS referencia, transacciones.monto AS monto, transacciones.fecha AS fecha, transacciones.empleado_gestion AS empleado_gestion, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.habilitarnuevoscreditos AS habilitarnuevoscreditos FROM ((transacciones join usuarios on(transacciones.idusuarios = usuarios.idusuarios)) join cuotas on(cuotas.idcuotas = transacciones.idcuotas))  ;


CREATE VIEW vista_consultatransaccionescuentasclientes  AS SELECT transaccionescuentasclientes.idtransaccioncuentas AS idtransaccioncuentas, transaccionescuentasclientes.idusuarios AS idusuarios, transaccionescuentasclientes.idproducto AS idproducto, transaccionescuentasclientes.idcuentas AS idcuentas, cuentas.numerocuenta AS numerocuenta, productos.codigo AS codigo, productos.nombreproducto AS nombreproducto, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, transaccionescuentasclientes.referencia AS referencia, transaccionescuentasclientes.monto AS monto, transaccionescuentasclientes.fecha AS fecha, transaccionescuentasclientes.empleado_gestion AS empleado_gestion, transaccionescuentasclientes.tipotransaccion AS tipotransaccion, transaccionescuentasclientes.estadotransaccion AS estadotransaccion, transaccionescuentasclientes.saldonuevocuenta_transaccion AS saldonuevocuenta_transaccion FROM ((((transaccionescuentasclientes join usuarios on(transaccionescuentasclientes.idusuarios = usuarios.idusuarios)) join detalleusuarios on(transaccionescuentasclientes.idusuarios = detalleusuarios.idusuarios)) join productos on(transaccionescuentasclientes.idproducto = productos.idproducto)) join cuentas on(transaccionescuentasclientes.idcuentas = cuentas.idcuentas))  ;


CREATE VIEW vista_consultausuariosnuevascuentas_ahorros_depositosplazofijo  AS SELECT usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, detalleusuarios.dui AS dui FROM (usuarios join detalleusuarios on(usuarios.idusuarios = detalleusuarios.idusuarios))  ;


CREATE VIEW vista_consultausuariosregistrados  AS SELECT count(usuarios.idusuarios) AS numero_usuarios_registrados FROM usuarios  ;


CREATE VIEW vista_consulta_asignarnuevoscreditosclientes  AS SELECT usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.correo AS correo, usuarios.fotoperfil AS fotoperfil, usuarios.completoperfil AS completoperfil, usuarios.estado_usuario AS estado_usuario, usuarios.habilitarnuevoscreditos AS habilitarnuevoscreditos, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, detalleusuarios.telefono AS telefono, detalleusuarios.celular AS celular, detalleusuarios.telefonotrabajo AS telefonotrabajo, detalleusuarios.direccion AS direccion, detalleusuarios.empresa AS empresa, detalleusuarios.cargo AS cargo, detalleusuarios.direcciontrabajo AS direcciontrabajo, detalleusuarios.fechanacimiento AS fechanacimiento, detalleusuarios.genero AS genero, detalleusuarios.estadocivil AS estadocivil, detalleusuarios.fotoduifrontal AS fotoduifrontal, detalleusuarios.fotoduireverso AS fotoduireverso, detalleusuarios.fotonit AS fotonit, detalleusuarios.fotofirma AS fotofirma FROM (usuarios left join detalleusuarios on(usuarios.idusuarios = detalleusuarios.idusuarios))  ; 


CREATE VIEW vista_numerocuotasregistradas  AS SELECT count(cuotas.idcuotas) AS numero_cuotas FROM cuotas  ;

CREATE VIEW vista_numerotransaccionesprocesadas  AS SELECT count(transacciones.idtransaccion) AS numero_transacciones FROM transacciones  ;


CREATE VIEW vista_consulta_datosgeneralesresultadosinicioadmins  AS SELECT vista_consultausuariosregistrados.numero_usuarios_registrados AS numero_usuarios_registrados, vista_consultaproductosregistrados.numero_productos_registrados AS numero_productos_registrados, vista_consultanumeroreportesfallasplataformageneral.numero_reportes_registrados AS numero_reportes_registrados, vista_consultanumerosolicitudesrecuperaciones.numero_solicitudes_recuperaciones AS numero_solicitudes_recuperaciones, vista_numerocuotasregistradas.numero_cuotas AS numero_cuotas, vista_numerotransaccionesprocesadas.numero_transacciones AS numero_transacciones FROM (((((vista_consultausuariosregistrados left join vista_consultaproductosregistrados on(vista_consultausuariosregistrados.numero_usuarios_registrados = vista_consultausuariosregistrados.numero_usuarios_registrados)) left join vista_consultanumeroreportesfallasplataformageneral on(vista_consultausuariosregistrados.numero_usuarios_registrados = vista_consultausuariosregistrados.numero_usuarios_registrados)) left join vista_consultanumerosolicitudesrecuperaciones on(vista_consultausuariosregistrados.numero_usuarios_registrados = vista_consultausuariosregistrados.numero_usuarios_registrados)) left join vista_numerocuotasregistradas on(vista_consultausuariosregistrados.numero_usuarios_registrados = vista_consultausuariosregistrados.numero_usuarios_registrados)) left join vista_numerotransaccionesprocesadas on(vista_consultausuariosregistrados.numero_usuarios_registrados = vista_consultausuariosregistrados.numero_usuarios_registrados))  ;

CREATE VIEW vista_contadorcreditosgestionados_empleadosatencionclientes  AS SELECT creditos.usuario_empleado AS usuario_empleado, count(0) AS numero_creditos_gestionados FROM creditos GROUP BY creditos.usuario_empleado ;


CREATE VIEW vista_contadorincumplimientopagoscreditosclientes  AS SELECT usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, count(0) AS total_incumplimientos_pagos FROM (cuotas join usuarios on(cuotas.idusuarios = usuarios.idusuarios)) WHERE cuotas.incumplimiento_pago = 'PT' GROUP BY usuarios.idusuarios  ;


CREATE VIEW vista_contadorpagosatiempo_creditosclientes  AS SELECT cuotas.idusuarios AS idusuarios, creditos.idcreditos AS idcreditos, cuotas.idcuotas AS idcuotas, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, creditos.estadocrediticio AS estadocrediticio, count(cuotas.incumplimiento_pago) AS cuotas_pagadas FROM ((cuotas join usuarios on(cuotas.idusuarios = usuarios.idusuarios)) join creditos on(creditos.idusuarios = usuarios.idusuarios)) WHERE cuotas.incumplimiento_pago = 'NO' AND cuotas.estadocuota = 'cancelado' GROUP BY cuotas.idusuarios  ;


CREATE VIEW vista_contadorpagoscuotastardias_creditosclientes  AS SELECT cuotas.idusuarios AS idusuarios, creditos.idcreditos AS idcreditos, cuotas.idcuotas AS idcuotas, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, creditos.estadocrediticio AS estadocrediticio, count(cuotas.incumplimiento_pago) AS cuotas_pagadas_tarde FROM ((cuotas join usuarios on(cuotas.idusuarios = usuarios.idusuarios)) join creditos on(creditos.idusuarios = usuarios.idusuarios)) WHERE cuotas.incumplimiento_pago = 'PT' GROUP BY cuotas.idusuarios  ;


CREATE VIEW vista_contadortransacciones_empleadosatencionclientes  AS SELECT count(0) AS numero_transacciones_empleados_atencionclientes, transacciones.empleado_gestion AS empleado_gestion FROM transacciones GROUP BY transacciones.empleado_gestion  ;


CREATE VIEW vista_datosgeneralescreditoscanceladoshistoricoclientes  AS SELECT historicocreditos.idhistorico AS idhistorico, historicocreditos.idproducto AS idproducto, historicocreditos.idusuarios AS idusuarios, historicocreditos.idcreditos AS idcreditos, historicocreditos.montocredito AS montocredito, historicocreditos.interescredito AS interescredito, historicocreditos.plazocredito AS plazocredito, historicocreditos.cuotamensual AS cuotamensual, historicocreditos.estado AS estado, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.fotoperfil AS fotoperfil, productos.nombreproducto AS nombreproducto FROM ((historicocreditos join usuarios on(historicocreditos.idusuarios = usuarios.idusuarios)) join productos on(historicocreditos.idproducto = productos.idproducto))  ;


CREATE VIEW vista_detallecompletotransaccionesempleados  AS SELECT transacciones.idtransaccion AS idtransaccion, transacciones.idusuarios AS idusuarios, transacciones.idproducto AS idproducto, transacciones.idcreditos AS idcreditos, transacciones.idcuotas AS idcuotas, transacciones.referencia AS referencia, transacciones.monto AS monto, transacciones.fecha AS fecha, transacciones.dias_incumplimiento AS dias_incumplimiento, transacciones.empleado_gestion AS empleado_gestion FROM transacciones  ;


CREATE VIEW vista_detalleinterfazgerencia  AS SELECT vista_calculoproductosregistrados_interfaces.numero_productos_registrados AS numero_productos_registrados, vista_calculocuotasregistradas_interfaces.numero_cuotas_registrados AS numero_cuotas_registrados, vista_calculocuentasahorooregistradas_interfaces.numero_cuentasahorro_registradas AS numero_cuentasahorro_registradas, vista_calculotransaccionescreditos_interfaces.numero_transacciones_creditos AS numero_transacciones_creditos, vista_calculonumerocuotaspagadastarde_interfaces.numero_cuotas_pagadas_tarde AS numero_cuotas_pagadas_tarde, vista_calculonumerocuotascanceladas_interfaces.numero_cuotas_canceladas AS numero_cuotas_canceladas, vista_calculonumerotransferencias_interfaces.numero_transferencias AS numero_transferencias, vista_calculocuotasvencidas_interfaces.numero_cuotas_vencidas AS numero_cuotas_vencidas FROM (((((((vista_calculoproductosregistrados_interfaces left join vista_calculocuotasregistradas_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculocuentasahorooregistradas_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculotransaccionescreditos_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculonumerocuotaspagadastarde_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculonumerocuotascanceladas_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculonumerotransferencias_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados)) left join vista_calculocuotasvencidas_interfaces on(vista_calculoproductosregistrados_interfaces.numero_productos_registrados = vista_calculoproductosregistrados_interfaces.numero_productos_registrados))  ;


CREATE VIEW vista_detallesfacturacioncreditosclientes  AS SELECT cuotas.idcuotas AS idcuotas, cuotas.idcreditos AS idcreditos, cuotas.idproducto AS idproducto, cuotas.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, creditos.cuotamensual AS cuotamensual, cuotas.montocancelar AS montocancelar, cuotas.nombreproducto AS nombreproducto, productos.codigo AS codigo, cuotas.montocapital AS montocapital, cuotas.fechavencimiento AS fechavencimiento, transacciones.referencia AS referencia, transacciones.fecha AS fecha, transacciones.dias_incumplimiento AS dias_incumplimiento, transacciones.empleado_gestion AS empleado_gestion FROM (((((cuotas join usuarios on(cuotas.idusuarios = usuarios.idusuarios)) join detalleusuarios on(detalleusuarios.idusuarios = usuarios.idusuarios)) join transacciones on(transacciones.idcuotas = cuotas.idcuotas)) join creditos on(creditos.idusuarios = usuarios.idusuarios)) join productos on(productos.idproducto = cuotas.idproducto))  ;


CREATE VIEW vista_detallesfacturacioncreditosclienteshistoricos  AS SELECT historicocuotascreditos.idhistorico AS idhistorico, historicocuotascreditos.idcreditos AS idcreditos, historicotransacciones.idcuotas, historicocuotascreditos.idproducto AS idproducto, historicocuotascreditos.idusuarios AS idusuarios, historicocuotascreditos.montocancelar AS montocancelar, historicocuotascreditos.nombreproducto AS nombreproducto, historicocuotascreditos.montocapital AS montocapital, historicocuotascreditos.fechavencimiento AS fechavencimiento FROM historicocuotascreditos INNER JOIN historicotransacciones ON historicotransacciones.idhistoricotransaccion = historicocuotascreditos.idcreditos; ;


CREATE VIEW vista_detallesusuarios  AS SELECT detalleusuarios.iddetalle AS iddetalle, detalleusuarios.dui AS dui, detalleusuarios.nit AS nit, detalleusuarios.telefono AS telefono, detalleusuarios.celular AS celular, detalleusuarios.telefonotrabajo AS telefonotrabajo, detalleusuarios.direccion AS direccion, detalleusuarios.empresa AS empresa, detalleusuarios.cargo AS cargo, detalleusuarios.direcciontrabajo AS direcciontrabajo, detalleusuarios.fechanacimiento AS fechanacimiento, detalleusuarios.genero AS genero, detalleusuarios.estadocivil AS estadocivil, detalleusuarios.idusuarios AS idusuarios FROM detalleusuarios  ;


CREATE VIEW vista_notificacionesrecibidasusuarios  AS SELECT notificaciones.idnotificacion AS idnotificacion, notificaciones.idusuarios AS idusuarios, notificaciones.titulonotificacion AS titulonotificacion, notificaciones.descripcionnotificacion AS descripcionnotificacion, notificaciones.fechanotificacion AS fechanotificacion, notificaciones.clasificacionnotificacion AS clasificacionnotificacion, notificaciones.ocultarnotificacion AS ocultarnotificacion FROM notificaciones  ;



CREATE VIEW vista_productoscashmanha  AS SELECT productos.idproducto AS idproducto, productos.codigo AS codigo, productos.nombreproducto AS nombreproducto, productos.descripcionproducto AS descripcionproducto, productos.requisitosproductos AS requisitosproductos, productos.estado AS estado FROM productos  ;


CREATE VIEW vista_reporteiniciosdesesiones  AS SELECT accesos.idacceso AS idacceso, accesos.fechaacceso AS fechaacceso, accesos.dispositivo AS dispositivo, accesos.sistemaoperativo AS sistemaoperativo, accesos.idusuarios AS idusuarios FROM accesos  ;


CREATE VIEW vista_rolesdeusuarioscompleto  AS SELECT roles.idrol AS idrol, roles.nombrerol AS nombrerol, roles.descripcionrol AS descripcionrol FROM roles  ;


CREATE VIEW vista_transaccionespagocuotascreditosclientes  AS SELECT transacciones.idtransaccion AS idtransaccion, transacciones.idusuarios AS idusuarios, transacciones.idcuotas AS idcuotas, transacciones.referencia AS referencia, transacciones.monto AS monto, transacciones.fecha AS fecha FROM transacciones  ;


CREATE VIEW vista_usuariosperfilincompleto  AS SELECT usuarios.idusuarios AS idusuarios, usuarios.nombres AS nombres, usuarios.apellidos AS apellidos, usuarios.codigousuario AS codigousuario, usuarios.correo AS correo, usuarios.fotoperfil AS fotoperfil, usuarios.idrol AS idrol, usuarios.estado_usuario AS estado_usuario, usuarios.completoperfil AS completoperfil, usuarios.quienregistro AS quienregistro FROM usuarios  ;
